@include('themes.main.footer')

<script>
    new Swiper('.image-carousel', {
        speed: 1000,
        slidesPerView: 1,
        navigation: {
            prevEl: '.image-carousel-prev',
            nextEl: '.image-carousel-next',
        },
    });

    $('#page-about .inner .map-field .map .point').each(function() {
        let name = $(this).data('name'),
            path = $('#page-about .inner .map-field .map svg path[data-name="' + name + '"]')[0];
        wrapper = $('#page-about .inner .map-field .map svg')[0],
            x = path.getBoundingClientRect().left - wrapper.getBoundingClientRect().left + (path
                .getBoundingClientRect().width / 2) - $(this).children('.dot').width() / 2,
            y = path.getBoundingClientRect().top - wrapper.getBoundingClientRect().top + (path
                .getBoundingClientRect().height / 2) - $(this).children('.dot').height() / 2;

        console.log(x);
        $(this).css('left', x + 'px');
        $(this).css('top', y + 'px');
    });
</script>
